import React from 'react';

const BackendErrorMessages = (props) => {

    let errors = [];

    for (let key in props.errors) {
        let error = `${key} ${props.errors[key]}`;
        errors.push(error);
    }

    console.log(errors);

    return(
        <div>
            {errors.map(elem => <div>{elem}</div>)}
        </div>
    )
}

export default BackendErrorMessages;